el:protected.el:luamain:
wo["FollowMethod"] = FOLLOW_SCROLL

ti[" "] = {"fl_sand"}
ti["#"] = {"st_greenbrown"}
ti["-"] = {"st_nil"}

local width = 37
local height = 24

themaze = res.maze(ti, {area={po(1,1), width, height}, render={{"-", " "}, "#", " "}})
wo(themaze, " ", 2*width+1, 2*height+1)
wo:drawBorder(po(0,0), wo["Width"], wo["Height"], ti["#"])
wo[po(1,0).. po(1,2*height) .. po(2*width-1,0) .. po(2*width-1,2*height)] = {"st_oxyd"}
wo:shuffleOxyd()

wo[po(2*random(width)-1, 2*random(height)-1)] = {"#ac_marble"}
