el:protected.el:luamain:
wo["SlopeStrength"] = 30

ti["v"] = {"fl_slope_ps"}
ti["^"] = {"fl_slope_pn"}
ti[">"] = {"fl_slope_pe"}
ti["<"] = {"fl_slope_pw"}
ti["B"] = {"fl_slope_ose"}
ti["A"] = {"fl_slope_osw"}
ti["C"] = {"fl_slope_one"}
ti["D"] = {"fl_slope_onw"}
ti["b"] = {"fl_slope_ise"}
ti["c"] = {"fl_slope_ine"}
ti["a"] = {"fl_slope_isw"}
ti["d"] = {"fl_slope_inw"}
ti[" "] = {"fl_space"}
ti["#"] = {"st_death"}
ti["O"] = {"st_oxyd", flavor = "b"}

-- Note that in the following, gradient "d" is missing.
-- This is historical.
random_gradients = {"v", "^", ">", "<", "B", "A", "C", "D", "b", "c", "a"}

if wo["IsDifficult"] then
    ti["@"] = {"#ac_marble", adhesion = 1} .. ti["B"]
    w, h = wo(res.random(ti, "R", random_gradients), " ",
         {"####################",
          "#d^^^^^^^^^^^^^^^^c#",
          "#<O              O>#",
          "#< R R R @A R R R >#",
          "#<       CD       >#",
          "#< R R R    R R R >#",
          "#<       OO       >#",
          "#< R R R    R R R >#",
          "#<       BA       >#",
          "#< R R R CD R R R >#",
          "#<O              O>#",
          "#avvvvvvvvvvvvvvvvb#",
          "####################"})
else
    ti["@"] = {"#ac_marble", adhesion = 1} .. ti["d"]
    w, h = wo(res.random(ti, "R", random_gradients), " ",
         {"####################",
          "#BvvvvvvvvvvvvvvvvA#",
          "#>O              O<#",
          "#> R R R @c R R R <#",
          "#>       ab       <#",
          "#> R R R    R R R <#",
          "#>       OO       <#",
          "#> R R R    R R R <#",
          "#>       dc       <#",
          "#> R R R ab R R R <#",
          "#>O              O<#",
          "#C^^^^^^^^^^^^^^^^D#",
          "####################"})
end

wo:shuffleOxyd()
