function postinit()
    st(no["trigger%5"]+po(-1,0)):open()
end

function send_toggle(value, sender)
    if value == true then
        gr = grp({st(sender+po(-1,0)), st(sender+NNW), st(sender+SSW)})
        if sender.name == "trigger%1" then
            gr = grp({st(sender+NNW), st(sender+SSW)})
        end
        gr:toggle()
    end
end
