clock_to_delta_pos = { [NORTH] = po(0, -1), [EAST] = po(1, 0), [SOUTH] = po(0, 1), [WEST] = po(-1, 0) }
ti[" "] = {"fl_abyss"} ti["."] = {"fl_brick"} ti["O"] = {"st_oxyd", flavor = "a", oxydcolor = OXYD_BLACK} .. ti[" "] ti["@"] = {"#ac_marble", adhesion = 1} ti["!"] = {"st_purplemarble"} ti["#"] = {"st_ghost_purplemarble"} ti["b"] = {"it_brush", "thisbrush"} ti["T"] = {"st_timer", "timer", target = "callback_timer", interval = 0.8} ti["C"] = {"st_fourswitch", "clock"}
w, h = wo(ti, ".", {"T!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!", "!@.#......#........#....... !", "!..#...#..###...####....... !", "!.b#####....#...#.......... !", "!........#..#.#.#..#....... !", "!######..#....#.#..#....... !", "!.....#..#.#..#.#..#....... !", "!...#.#.##.#..#.#..#....... !", "!...#.###..####.#..#....... !", "!##.#......#....#..#....... !", "!C..#...####.####..#....... !", "!...#......#.......#....... !", "!!!!!!!!!!!!!!!!!!!!!!!!!!! !", "! !", "! !", "! !", "! !", "! !", "! !", "! O O !", "! !", "! !", "! !", "! !", "!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!"})
wo:shuffleOxyd()
local step_count = 0 local painting = false local hint = false function callback_timer() if no["clock"]:exists() then local clock_state = no["clock"].state local old_pos = po(no["clock"]) local clock_pos = old_pos + clock_to_delta_pos[clock_state] -- The clock is blocked by stones, so we don't need to -- clip clock_pos to the level size. -- Change direction each 4 steps (yes, prior to move) step_count = step_count + 1 if step_count == 5 then no["clock"].counterclock = not wo["IsDifficult"] no["clock"]:toggle() no["clock"].counterclock = false clock_state = no["clock"].state step_count = 1 end -- Move stone if st(clock_pos):exists() then clock_pos = old_pos else wo[old_pos] = {"st_nil"} wo[clock_pos] = ti["C"] end -- Adapt direction no["clock"].state = clock_state -- Maybe there is some item under the new position? if it(clock_pos):is("it_brush") then wo[clock_pos] = {"it_nil"} painting = true end -- Paint your way to a brighter day ... if painting then wo[clock_pos] = {"fl_concrete"} end -- In case the clock is still hungry and in the dark ... if (not painting) and (not hint) and (clock_pos.y > 12) then wo[{1,1}] = {"it_document", text = "text1"} hint = true end end end
