ti["m"] = ti["-"] .. {"it_magnet", name="magnets#"}
ti["O"] = {"st_oxyd"}
ti["@"] = ti["-"] .. {"#ac_marble_black"}

wo(ti, " ", {
    "####################",
    "O #@-----mm------# O",
    "# #--------------# #",
    "# #-m----------m-# #",
    "# #--------------# #",
    "# D--------------D #",
    "# D---m--==--m---D #",
