local trinity = {0, 0, 0, 0, 0, 0, 0, 0}

function callback(value, sender)
    trinity[fl(sender)._number] = trinity[fl(sender)._number] + 1
    if trinity[fl(sender)._number] == 3 then
        no["doors#*"]:close()
    end
end

function reset(value, sender)
    trinity = {0, 0, 0, 0, 0, 0, 0, 0}
    no["doors#*"]:open()
end
