function chess(value, sender)
    if st(sender+E):is("st_chess") then
        no["bridge"]:close()
        grp(no["laser1"], no["laser2"]):off()
    end
end

function dry(value, sender)
    sender["target"] = DEFAULT
    wo:drawMap(ti, po(5, 3), " ", {"-","-","-","-","-","-","-","-"})
end
