el:protected.el:luamain:
wo["FollowGrid"] = true
wo["FollowMethod"] = FOLLOW_SCROLL
wo["FollowAction"] = FOLLOW_HALFSCREEN

ti[" "] = {"fl_water"}
ti["="] = {"fl_gray"}
ti["v"] = {"fl_slope_ps"}
ti["^"] = {"fl_slope_pn"}
ti[">"] = {"fl_slope_pe"}
ti["<"] = {"fl_slope_pw"}
ti["7"] = {"fl_slope_ose"}
ti["9"] = {"fl_slope_osw"}
ti["1"] = {"fl_slope_one"}
ti["3"] = {"fl_slope_onw"}
ti["W"] = {"st_passage", color=WHITE, flavor="frame"}
ti["O"] = {"st_oxyd"}
ti["A"] = ti["="] .. {"#ac_marble_white", controllers=CTRL_NONE, essential=INDISPENSABLE}
ti["@"] = ti["="] .. {"#ac_marble_black"}

function passageresolver(key, x, y)
    if key == "O" then
        wo:drawMap(ti, po(x-1, y-1), "-", {"-W-",
                                           "WOW",
                                           "-W-"})
        return ti({})
    else
        return ti[key]
    end
end

wo(passageresolver, " ", {
    "                                       ",
    " Ovvvvvvvvvvvv97OvvvvvO97vvvvvvvvvvvvO ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >=@==<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >=A==<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " >====<<<<<<<<<>=======<>>>>>>>>>>===< ",
    " O^^^^^^^^^^^^31O^^^^^O31^^^^^^^^^^^^O ",
    "                                       "})

wo:shuffleOxyd()
