function chess(value, sender)
    if no["stablewhite#*"] == fl(no["chesswhite#*"]) then
        no["oxydwhite#*"]:open()
    end
    if no["stableblack#*"] == fl(no["chessblack#*"]) then
        no["oxydblack#*"]:open()
    end
end
