local fourswitches = no["fourswitch#*"]

function callback_fourswitch(is_on, sender)
    wo[sender + po(0,22)] = floors[sender.state]
    for fourswitch in fourswitches do
        if fourswitch.state ~= st(fourswitch + N)._correct then
            no["oxyd_door#*"]:close()
            return
        end
    end
    no["oxyd_door#*"]:open()
end
