function ac_blackball(x,y)
n=""
p=0
f=0
 if (x==0) and (y==0) then
  n="ac0x0"
  p=0
  mf=1
 end
set_actor("ac-blackball",x+0.5,y+0.5,{player=p,name=n,mouseforce=mf})
end
