function ac_top(x,y)
n=""
r=0
f=0
 init=false
 if (x==7) and (y==4) then
  n="ac7x4"
  r=1
  f=1
  init=FALSE
 end
 if (x==10) and (y==27) then
  n="ac10x27"
  r=1
  f=1
  init=FALSE
 end
 if (x==13) and (y==27) then
  n="ac13x27"
  r=1
  f=1
  init=FALSE
 end
set_actor("ac-top",x+0.5,y+0.5,{range=r,force=f,name=n,gohome=init})
end
