ti["o "] = ti["  "] .. {"st_oxyd", name = "north#"}
ti["O "] = ti["  "] .. {"st_oxyd", name = "south#"}
ti["@b"] = ti["  "] .. {"#ac_marble_black", essential = INDISPENSIBLE}
ti["@w"] = ti["  "] .. {"#ac_marble_white", essential = INDISPENSIBLE}
ti["P"] = ti["  "] .. {"st_puzzle_blue", intensity = 4}

if wo["IsDifficult"] then
    ti[".."] = {"fl_abyss"}
    ti["##"] = ti[".."] .. {"st_rawglass"}
    ti["++"] = ti[".."]
    ti["**"] = ti["##"]
    ti["S"] = ti[".."] .. {"st_puzzle_blue", intensity = 0}
else
    ti[".."] = {"fl_water"}
    ti["##"] = ti[".."] .. {"st_rawglass"}
    ti["++"] = ti[".."] .. ti["##"]
    ti["**"] = ti[".."]
    ti["S"] = ti[".."] .. {"st_puzzle", cluster = 1, intensity = 5}
end

w, h = wo(res.puzzle(ti, "P", "S"), "..", {
    "      wo          @b            wo      ",
    "  o   wo            P1P1        wo  o   ",
    "  o   wo              P1        wo  o   ",
    "  o   wo              P1P1P1    wo  o   ",
    "      wo....##......**##++##....wo      ",
    "............##....####++..##............",
    "++++++++++++##**SiSbShSf**##++++++++++++",
    "............##..++####....##............",
    "      bo....##++##**......##....bo      ",
    "  O   bo    P1P1P1              bo  O   ",
    "  O   bo        P1              bo  O   ",
    "  O   bo        P1P1            bo  O   ",
    "      bo            @w          bo      "
})

wo:shuffleOxyd({"north#*", max = 0}, {"south#*", max = 0})

